            $subject->name = $row["val1"];
            $subject->code = $row["val0"];
            $subject->parent_id = 0;
            $subject->save();
        }

        foreach ($chapters as $row) {
            $subject = new Subject();
            $subject->name = $row["val2"];
            $subject->code = $row["val0"];
            $subject->parent_id = $row["val1"];
            $subject->save();
        }
    }
}
